/**
 * Main App Component
 *
 * Root component for the Food Recognition application
 */

import React, { useState } from 'react';
import ImageUpload from './components/ImageUpload';
import ResultsDisplay from './components/ResultsDisplay';
import './styles/App.css';

function App() {
  const [selectedImage, setSelectedImage] = useState(null);
  const [previewUrl, setPreviewUrl] = useState(null);
  const [loading, setLoading] = useState(false);
  const [results, setResults] = useState(null);
  const [error, setError] = useState(null);

  /**
   * Handle image selection
   */
  const handleImageSelect = (file) => {
    setSelectedImage(file);
    setPreviewUrl(URL.createObjectURL(file));
    setError(null);
    setResults(null);
  };

  /**
   * Handle analysis completion
   */
  const handleAnalysisComplete = (analysisResults) => {
    setResults(analysisResults);
    setLoading(false);
  };

  /**
   * Handle analysis error
   */
  const handleAnalysisError = (errorMessage) => {
    setError(errorMessage);
    setLoading(false);
  };

  /**
   * Handle analysis start
   */
  const handleAnalysisStart = () => {
    setLoading(true);
    setError(null);
    setResults(null);
  };

  /**
   * Reset to upload new image
   */
  const handleReset = () => {
    setSelectedImage(null);
    setPreviewUrl(null);
    setResults(null);
    setError(null);
    setLoading(false);
  };

  return (
    <div className="App">
      <div className="app-container">
        {/* Header */}
        <header className="app-header">
          <h1>AI Food Recognition</h1>
          <p className="tagline">
            Identify food and get instant nutrition analysis
          </p>
        </header>

        {/* Main Content */}
        <main className="app-main">
          {!results ? (
            <ImageUpload
              selectedImage={selectedImage}
              previewUrl={previewUrl}
              loading={loading}
              onImageSelect={handleImageSelect}
              onAnalysisStart={handleAnalysisStart}
              onAnalysisComplete={handleAnalysisComplete}
              onAnalysisError={handleAnalysisError}
            />
          ) : (
            <ResultsDisplay
              results={results}
              previewUrl={previewUrl}
              onReset={handleReset}
            />
          )}

          {/* Error Display */}
          {error && (
            <div className="error-message">
              <div className="error-icon">âš </div>
              <p>{error}</p>
              <button onClick={handleReset} className="btn-secondary">
                Try Again
              </button>
            </div>
          )}
        </main>

        {/* Footer */}
        <footer className="app-footer">
          <p>
            Powered by EfficientNet | USDA FoodData Central | OpenAI GPT
          </p>
          <p className="footer-note">
            BSc Computer Science Final Year Project
          </p>
        </footer>
      </div>
    </div>
  );
}

export default App;
